import React, { Component } from "react";
import P5Wrapper from "react-p5-wrapper";
import "./About.css";

class About extends Component {
  sketch1 = p => {
    let num = 2000;
    let range = 6;

    let ax = [];
    let ay = [];

    p.setup = function() {
      p.createCanvas(710, 400);
      for (let i = 0; i < num; i++) {
        ax[i] = 710 / 2;
        ay[i] = 400 / 2;
      }
      p.frameRate(30);
    };

    p.draw = function() {
      p.background(51);

      // Shift all elements 1 place to the left
      for (let i = 1; i < num; i++) {
        ax[i - 1] = ax[i];
        ay[i - 1] = ay[i];
      }

      // Put a new value at the end of the array
      ax[num - 1] += p.random(-range, range);
      ay[num - 1] += p.random(-range, range);

      // Constrain all points to the screen
      ax[num - 1] = p.constrain(ax[num - 1], 0, 710);
      ay[num - 1] = p.constrain(ay[num - 1], 0, 400);

      // Draw a line connecting the points
      for (let j = 1; j < num; j++) {
        let val = (j / num) * 204.0 + 51;
        p.stroke(val);
        p.line(ax[j - 1], ay[j - 1], ax[j], ay[j]);
      }
    };
  };
  render() {
    return (
      <div className="container">
        <P5Wrapper className="p5" sketch={this.sketch1} />
        <h3>About</h3>
      </div>
    );
  }
}

export default About;
